mapper.select:


      select t.rn,t.userid,t.loginname,t.loginpwd,t.deptno,t.is_dele ,t.roleid from
        (select rownum rn,u.userid,u.loginname,u.loginpwd,u.deptno,u.is_dele, utr.roleid from
         sysuser u left join usertorole utr on u.userid=utr.userid left join sysrole sr on utr.roleid=sr.roleid 

			 and u.loginname like '%${loginname}%' 
			 and u.is_dele = #{is_dele} 
			 and u.deptno = #{deptno} 
			 and utr.roleid = #{roleid} 

        )t where rn > #{start} and rn <= #{end}

select count(u.userid) from 
sysuser u left join usertorole utr on u.userid=utr.userid left join sysrole sr on utr.roleid=sr.roleid 

	 and u.loginname like '%${loginname}%' 
	 and u.is_dele = #{is_dele} 
	 and u.deptno = #{deptno} 
	 and utr.roleid = #{roleid}

mapper.update:
update sysuser
loginname=#{loginname}, deptno=#{deptno}, is_dele=#{is_dele},
where userid=#{userid}

mapper.select:

select rownum rn,u.userid,u.loginname,u.loginpwd,u.deptno,u.is_dele, utr.roleid from 
sysuser u left join usertorole utr on u.userid=utr.userid left join sysrole sr on utr.roleid=sr.roleid 
where u.userid = #{userid}
